const SIX_HOURS = 6 * 60 * 60 * 1000;
let isKilled = false;
let uiShadowRoot = null;

// --- MESSAGE LISTENER ---
browser.runtime.onMessage.addListener((request) => {
  if (request.action === "analyze_vibe") performVibeCheck();
  if (request.action === "scan_heatmap") performHeatmapScan();
});

// --- HEATMAP FUNCTION ---
function performHeatmapScan() {
  // Looks for numbers next to 'Viewing' or inside forum stats
  const rows = document.querySelectorAll('tr, li.forum-row, div.node-body');
  if(rows.length === 0) return alert("No forum sections found. Are you on the Homepage?");

  let maxViewers = 0;
  rows.forEach(row => {
     const text = row.innerText;
     // Regex to find '123 Viewing' or just raw numbers in stat columns
     const match = text.match(/(\d+)\s?Viewing/i) || text.match(/Viewing:\s?(\d+)/i);
     if(match) {
       const num = parseInt(match[1]);
       if(num > maxViewers) maxViewers = num;
       row.dataset.viewers = num;
     }
  });

  rows.forEach(row => {
    if(row.dataset.viewers) {
      const val = parseInt(row.dataset.viewers);
      if (val > (maxViewers * 0.7)) {
         row.style.border = "2px solid red";
         row.style.backgroundColor = "rgba(255, 0, 0, 0.1)";
         row.setAttribute('title', 'üî• HOT SECTION: High Traffic');
      } else if (val > (maxViewers * 0.3)) {
         row.style.border = "2px solid orange";
         row.style.backgroundColor = "rgba(255, 165, 0, 0.1)";
      }
    }
  });
  alert("üî• Scan Complete! Red sections are the busiest.");
}

// --- VIBE CHECK ---
async function performVibeCheck() {
  const data = await browser.storage.local.get('apiKey');
  if (!data.apiKey) return alert("Please save API Key first.");

  const pageText = document.body.innerText.substring(0, 3000);
  const learningNotification = document.createElement('div');
  learningNotification.style = "position:fixed; top:10px; right:10px; background:#8b5cf6; color:white; padding:15px; border-radius:8px; z-index:999999; font-family:sans-serif; font-weight:bold; box-shadow:0 5px 15px rgba(0,0,0,0.2);";
  learningNotification.innerText = "üîÆ Analyzing Section Vibe...";
  document.body.appendChild(learningNotification);

  try {
    const response = await fetch('https://api.openai.com/v1/chat/completions', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${data.apiKey}` },
      body: JSON.stringify({
        model: "gpt-4o-mini",
        messages: [{
          role: "system",
          content: "Analyze forum text. Output System Prompt mimicking tone. If Lounge/General: Chill, simple, 2023 account. If Business: Expert."
        }, {
          role: "user",
          content: `Context: ${pageText}`
        }]
      })
    });

    const result = await response.json();
    const newPersona = result.choices[0].message.content;

    await browser.storage.local.set({ customPersona: newPersona });
    learningNotification.style.background = "#10a37f";
    learningNotification.innerText = "‚úÖ Vibe Learned!";
    setTimeout(() => learningNotification.remove(), 2000);
  } catch (err) {
    alert("Analysis Failed: " + err.message);
    learningNotification.remove();
  }
}

async function initStealthBot() {
  const data = await browser.storage.local.get(['apiKey', 'repliedThreads', 'customPersona']);
  if (!data.apiKey) return;

  let modeText = "Amazon Expert Mode";
  if (data.customPersona) modeText = "Custom Vibe Active";
  else if (document.title.match(/Lounge|General|Off-Topic/i)) modeText = "Auto-Lounge Mode üõãÔ∏è";
  else if (document.title.match(/Intro|Welcome/i)) modeText = "Intro Mode üëã";

  createShadowUI(modeText);

  const threadId = window.location.href.split(/[?#]/)[0];
  const history = data.repliedThreads || {};
  if (history[threadId] && (Date.now() - history[threadId] < SIX_HOURS)) {
    uiShadowRoot.getElementById('sb-status').innerText = "Skipping (Replied recently)";
    return;
  }

  let attempts = 0;
  const finder = setInterval(() => {
    attempts++;
    const box = hasReplyBox();
    if (box) {
      clearInterval(finder);
      uiShadowRoot.getElementById('sb-status').innerText = "Target Found. Generating...";
      generateResponse(data.apiKey, threadId, data.customPersona);
    } else if (attempts > 5) {
      clearInterval(finder);
      uiShadowRoot.getElementById('sb-status').innerText = "No Editor Found.";
    }
  }, 1000);
}

function hasReplyBox() {
  return document.querySelector('textarea, [contenteditable="true"], .cke_editable, .fr-element');
}

function createShadowUI(modeText) {
  if (document.getElementById('amz-partner-host')) return;
  const host = document.createElement('div'); host.id = 'amz-partner-host';
  host.style.position = 'absolute'; host.style.top = '0'; host.style.left = '0'; host.style.zIndex = '2147483647';
  document.body.appendChild(host);
  uiShadowRoot = host.attachShadow({ mode: 'open' });

  const container = document.createElement('div');
  container.style = "position: fixed; top: 20px; right: 20px; width: 260px; background: #fff; border-left: 5px solid " + (modeText.includes('Lounge') ? '#f59e0b' : '#10a37f') + "; border-radius: 5px; padding: 15px; font-family: sans-serif; box-shadow: 0 5px 20px rgba(0,0,0,0.2); z-index: 9999;";
  container.innerHTML = `
    <div style="font-size:10px; color:" + (modeText.includes('Lounge') ? '#f59e0b' : '#10a37f') + "; font-weight:bold; margin-bottom:5px;">${modeText}</div>
    <div id="sb-status" style="font-weight:bold; color:#333; font-size:12px; margin-bottom:8px;">Scanning...</div>
    <div id="sb-summary" style="font-size:11px; color:#666; margin-bottom:8px;"></div>
    <div style="font-size:9px; color:#999; border-top:1px solid #eee; padding-top:5px;">Alt+9: Regen | Alt+Shift+K: Kill</div>
  `;
  uiShadowRoot.appendChild(container);
}

async function generateResponse(apiKey, threadId, customPersona) {
  if (isKilled) return;
  const status = uiShadowRoot.getElementById('sb-status');
  const summaryBox = uiShadowRoot.getElementById('sb-summary');

  const mem = await browser.storage.local.get('learnedExamples');
  const examples = mem.learnedExamples || [];
  const cheatSheet = examples.slice(-5).map(t => `"${t}"`).join("\n");

  let systemPrompt = `
    You are a forum user. Account Age: 2023.
    RULES:
    1. NEVER say "back in the day" or "old school".
    2. IF thread is List/Stats/Product: Short OBSERVATION only. No questions.
    3. IF thread is Intro: Welcome them warmly. Short.
    4. IF Lounge: Casual statement. "Yeah, crazy." 1 sentence.
    5. IF Business: Professional Amazon expert.
  `;

  if (customPersona) systemPrompt = customPersona + "\nREMEMBER: 2023 user rules apply.";
  else if (document.title.match(/Lounge|General|Off-Topic/i)) systemPrompt = "Casual Lounge user. 2023 account. Short, chill statement. 1 sentence.";
  else if (document.title.match(/Intro|Welcome/i)) systemPrompt = "Friendly user welcoming a newbie. Say 'Welcome to the forum, good luck!'.";

  if (cheatSheet) systemPrompt += `\n\nMIMIC THIS USER STYLE:\n${cheatSheet}`;

  const threadTitle = document.title;
  const threadBody = document.body.innerText.substring(0, 1500);

  try {
    const response = await fetch('https://api.openai.com/v1/chat/completions', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${apiKey}` },
      body: JSON.stringify({
        model: "gpt-4o-mini",
        messages: [{
          role: "system",
          content: systemPrompt + "\nIMPORTANT: Output format: SUMMARY: [text] || REPLY: [text]"
        }, {
          role: "user",
          content: `Thread Title: ${threadTitle}\nContext: ${threadBody}`
        }]
      })
    });
    const result = await response.json();
    const [summary, reply] = result.choices[0].message.content.split('||');

    summaryBox.innerHTML = `<b>Topic:</b> ${summary.replace('SUMMARY:', '').trim()}`;
    status.innerText = "Reply Ready.";
    status.style.color = "green";

    const cleanReply = reply.replace('REPLY:', '').trim();
    const box = hasReplyBox();
    if (box) {
      box.tagName === 'TEXTAREA' ? box.value = cleanReply : box.innerText = cleanReply;
      box.dispatchEvent(new Event('input', { bubbles: true }));
      box.scrollIntoView({ behavior: "smooth", block: "center" });
    }
  } catch (err) { status.innerText = "Error: " + err.message; }
}

document.addEventListener('click', (e) => {
  const target = e.target;
  if (target.matches('button, input[type="submit"], a.button') && /post|reply|submit/i.test(target.innerText || target.value)) {
    const threadId = window.location.href.split(/[?#]/)[0];
    browser.storage.local.get(['repliedThreads', 'learnedExamples', 'postCount']).then(d => {
      const updated = d.repliedThreads || {};
      updated[threadId] = Date.now();
      const box = hasReplyBox();
      const finalContent = box.tagName === 'TEXTAREA' ? box.value : box.innerText;
      const learned = d.learnedExamples || [];
      if (finalContent && finalContent.length > 5 && !learned.includes(finalContent)) {
        learned.push(finalContent);
      }
      browser.storage.local.set({ repliedThreads: updated, learnedExamples: learned, postCount: (d.postCount || 0) + 1 });
    });
  }
});

document.addEventListener('keydown', (e) => {
  if (e.altKey && e.shiftKey && e.code === 'KeyK') { document.getElementById('amz-partner-host').remove(); isKilled = true; }
  if (e.altKey && e.key === '9') location.reload();
});

initStealthBot();
